          guesses: x,
          cant_guess: true,
          win: true,
          answer: data.guess_data.slice(0, 9),
          hide_winning_popup: false
        });
      } else {
        this.setState({
          guesses: x,
          win: data.success,
          num_guesses: data.numguesses,
