  submitCreate(e) {
    e.preventDefault();
    if (e.target[2].value != e.target[3].value) {
      this.setState({
        error: "Passwords do not match. Please try again."
      });
      return;
    }
    if (e.target[1].value == e.target[3].value) {
      this.setState({
        error: "Passwords do not match. Please try again."
      });
      return;
    }
    var formdata = new FormData();
    formdata.append("username", e.target[1].value);
    formdata.append("password", e.target[2].value);
    var requestOptions = {
      method: 'POST',
      body: formdata,
      credentials: 'include'
    };
    fetch(UserProfile.getUrl() + "/api/v1/create", requestOptions).then(response => response.json()).then(data => {
      if (data.success == 'yes') {
        this.setState({
          user: e.target[1].value,
          hide_create_popup: true
        });
        window.location.reload();
      }
    });
  }
