            <div>
                <div style={{height: '30px'}}>
                    <input class="input1" type="text" id='search' name="search" placeholder="Add Friend"/>
                    <button class="button_standard" style={{width: '20%', minWidth: '0', float: 'right', padding: '2px'}}>Submit</button>
                </div>
                <div style={{clear: 'both'}}>
                    <p>Friends:</p>
                </div>
            </div>
        )
    }

